# Phase1_Reversion_Renames.ps1# LifeOS Phase 1 â€” Reversioning & Deprecation Audit# Purpose: Apply filename normalisation (version suffixes, naming cleanup) inside /docs.param(    [string]$RootPath = "C:\Users\cabra\Projects\LifeOS")$docs = Join-Path $RootPath "docs"Write-Host "Phase 1 Renames - starting in $docs"# Helper: safe renamefunction Rename-IfExists {    param(        [string]$OldPath,        [string]$NewPath    )    if (Test-Path $OldPath) {        $newName = Split-Path $NewPath -Leaf        Write-Host "Renaming:"        Write-Host "  $OldPath"        Write-Host "  -> $NewPath"        Rename-Item -Path $OldPath -NewName $newName    } else {        Write-Warning ("SKIP (missing): {0}" -f $OldPath)    }}# 00_foundationsRename-IfExists `    -OldPath (Join-Path $docs "00_foundations\Architecture_Skeleton.md") `    -NewPath (Join-Path $docs "00_foundations\Architecture_Skeleton_v1.0.md")# 01_governanceRename-IfExists `    -OldPath (Join-Path $docs "01_governance\COO_Expectations_Log.md") `    -NewPath (Join-Path $docs "01_governance\COO_Expectations_Log_v1.0.md")# Note: COO_Operating_Contract_v1.0.md and Antigravity_Council_Review_Packet_Spec_v1.0.md already conform.# 03_runtimeRename-IfExists `    -OldPath (Join-Path $docs "03_runtime\COO_Runtime_V1.1_Clean_Build_Spec.md") `    -NewPath (Join-Path $docs "03_runtime\COO_Runtime_Clean_Build_Spec_v1.1.md")Rename-IfExists `    -OldPath (Join-Path $docs "03_runtime\WALKTHROUGH.md") `    -NewPath (Join-Path $docs "03_runtime\COO_Runtime_Walkthrough_v1.0.md")# 04_project_builderRename-IfExists `    -OldPath (Join-Path $docs "04_project_builder\ProjectBuilder_Spec_v0.9_FinalClean.md") `    -NewPath (Join-Path $docs "04_project_builder\ProjectBuilder_Spec_v0.9_FinalClean_v1.0.md")# 05_agentsRename-IfExists `    -OldPath (Join-Path $docs "05_agents\COO_Agent_Mission_Orchestrator_Arch_v0.7_Aligned.md") `    -NewPath (Join-Path $docs "05_agents\COO_Agent_Mission_O